(#{name}
,#{category}
,#{sectionId}
,#{mainAddress}
,#{subAddress}        
,#{zipcode}        
,#{coordinates}       
,#{mainTelephone}        
,#{subTelephone}               
,#{firstImage}
,#{secondImage}        
,#{url}
,#{tagKeyword}
,#{tagMenu}
,#{tagService}
,#{open}
,#{nickName})

mapper.update:
UPDATE store SET store_name=CONCAT('[ν™•μΈν•„μ”!]', #{name})  
